import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useHistory
} from "react-router-dom";
import Navbar from "./Components/Navbar";
import Footer from "./Components/Footer";
import Popup from "./Components/Popup";
import About from "./Components/About";
import Project from "./Components/Project";
import LazyImage from "./Components/LazyImage";
import { Animated } from "react-animated-css";
import Images from "./images.json";
import "./styles.css";

export default function App() {
  return (
    <div className="App">
      <Router>
        <div className="website">
          <Navbar />
          <div className="main">
            <Switch>
              <Route path="/about">
                <About />
              </Route>
              <Route path="/project/:nameProject">
                <Project />
              </Route>
              <Route path="/contacts">
                <Contacts />
              </Route>
              <Route path="/gallery">
                <Gallery />
              </Route>
              <Route path="/">
                <Home />
              </Route>
            </Switch>
          </div>
          <Footer />
        </div>
      </Router>
    </div>
  );
}

function Home() {
  return (
    <div className="homeWrapper">
      <div className="homeIn">
        <Animated
          animationIn="fadeIn"
          animationOut="bounce"
          animationInDuration={1500}
          animationOutDuration={1000}
          isVisible={true}
        >
          <img className="logo" src="./image/logo.png" alt="logo" />
          <div>Kambiz Jalali</div>
          <span className="rabani">Architect</span>
          <div class="mdn">
            <Link to="/gallery">Gallery</Link>
            <Link to="/about">About</Link>
            <Link to="/contacts">Contact</Link>
          </div>
        </Animated>
      </div>
    </div>
  );
}

function Gallery({ id: inProp }) {
  const history = useHistory();
  const [open, setOpen] = useState(false);
  const [id, setId] = useState("");

  // const handleOPEN = (id) => {
  //   setId(id);
  //   setOpen(true);
  // };

  const handleChange = () => {
    setOpen(false);
    setId("");
  };

  return (
    <div>
      <div className="container">
        <div className="col-xs-12 ">
          {Images.map((image) => (
            <div key={image.id} className="wrapperPic">
              <div
                onClick={() => history.push(`/project/${image.parentName}`)}
                // onClick={() => {
                //   handleOPEN(image.parent);
                // }}
              >
                <LazyImage
                  className="demo"
                  aspectRatio={16 / 9}
                  lqip="data:image/jpeg;base64,"
                  src={"/image/index/" + image.name + ".jpg"}
                  alt="a woman covering face with frosted glass"
                />
              </div>
            </div>
          ))}
        </div>
      </div>
      {open ? <Popup onChange={handleChange} id={id} /> : ""}
    </div>
  );
}

function Contacts() {
  const [enable, setEnable] = useState(false);
  const [message, setMessage] = useState("Your message sent");
  const [showMessage, setShowMessage] = useState(false);
  const [typeMessage, setTypeMessage] = useState("success");

  const [isLoading, setLoading] = useState(false);

  function simulateNetworkRequest() {
    return new Promise((resolve) => setTimeout(resolve, 2000));
  }

  const handleClick = (event) => {
    event.preventDefault();
    setLoading(true);
    setEnable(true);
  };

  useEffect(() => {
    window.scrollTo(0, 0);

    if (isLoading) {
      simulateNetworkRequest().then(() => {
        setLoading(false);
        setShowMessage(true);
        setEnable(false);
      });
    }
  }, [isLoading]);

  return (
    <div className="container">
      <div className="contactform">
        <div>
          <div>
            <div className="wraperform">
              <div className="textcontact">
                You can consult us about these topics
              </div>
              <div className="nfk">
                <div>Architecture design</div>
                <div>Interior design</div>
                <div>Exterior design</div>
              </div>
              {showMessage ? (
                <div
                  className={
                    typeMessage === "success" ? "alertSuccess" : "alertError"
                  }
                >
                  {message}
                </div>
              ) : (
                ""
              )}
              <form onSubmit={handleClick}>
                <div className="winput">
                  <label htmlFor="name">Name</label>
                  <input disabled={enable} required id="name" type="text" />
                </div>
                <div className="winput">
                  <label htmlFor="email">Email</label>
                  <input disabled={enable} required id="email" type="email" />
                </div>
                <div className="winput">
                  <label htmlFor="message">Message</label>
                  <textarea disabled={enable} rows="4" required id="message" />
                </div>
                <div className="winput">
                  <button
                    type="submit"
                    disabled={isLoading}
                    //onClick={!isLoading ? handleClick : null}
                  >
                    {isLoading ? "Trying to sent..." : "SUBMIT"}
                  </button>
                </div>
                <div className="Masil">info@kambizjalali.com</div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
